mapper.select:
	SELECT minute_id, 
 	FROM t_stock_minute 
 	WHERE stock_code=#{stockCode} and date=#{date};

	SELECT minute_id, 
 	FROM t_stock_minute 
 	WHERE stock_code=#{stockCode} and date=#{date}
 	ORDER BY date, hour, minute;

	SELECT minute_id, 
 	FROM t_stock_minute 
 	WHERE stock_code=#{stockCode} and date>#{date}
 	ORDER BY date, hour, minute;
