mapper.select:
	SELECT minute_id, 
 	FROM t_stock_minute 
 	WHERE stock_code=#{stockCode} and date=#{date};

	SELECT minute_id, 
 	FROM t_stock_minute 
 	WHERE stock_code=#{stockCode} and date=#{date} and hour>=#{hour} and minute>=#{minute}
 	ORDER BY date, hour, minute LIMIT 0,1;

	SELECT minute_id, 
 	FROM t_stock_minute 
 	WHERE stock_code=#{stockCode} and date>#{date} and hour>=#{hour} and minute>=#{minute}
 	ORDER BY date, hour, minute LIMIT 0,1;

	SELECT minute_id, 
 	FROM t_stock_minute 
 	WHERE stock_code=#{stockCode} and date=#{date} and hour=#{hour} and minute=#{minute}
 	ORDER BY date, hour, minute LIMIT 0,1;
