mapper.select:
	SELECT 	daily_id, stock_id, stock_code, stock_name, DATE, last_close, OPEN, CLOSE, close_gap, close_gap_rate, 
		high, low, high_gap, high_gap_rate, low_gap, low_gap_rate, amount, volume, asset, isExrights, exrights, created, updated
 	FROM t_stock_daily 
 	WHERE stock_code=#{stockCode} and date < #{date}
 	ORDER BY date DESC
 	LIMIT 1 ;

mapper.update:
UPDATE t_stock_daily 
SET updated = sysdate(), last_close=#{lastClose}, close_gap=#{closeGap}, close_gap_rate=#{closeGapRate}, 
	high_gap=#{highGap}, high_gap_rate=#{highGapRate}, low_gap=#{lowGap}, low_gap_rate=#{lowGapRate}
WHERE stock_code=#{stockCode} and date=#{date};
